import React from "react";
import globe from "../../src/assets/img/globe2.png";
import doc from "../../src/assets/img/globe2.png";
import up from "../../src/assets/img/globe2.png";
import art from "../../src/assets/img/4.png";
import { Link } from "react-router-dom";
import DataFetchingComponent from "../component/DataFetchingComponent";

function About() {
  const articles = [
    { id: 1, image: art },
    { id: 2, image: globe },
    { id: 3, image: doc },
    { id: 4, image: up },
  ];

  return (
    <div className="main-4">
      <div className="product-4">
        <p>ARTICLE</p>
        <span>Follow all of our activities</span>
        <div className="content-4">
          <div className="owl-carousel">
            {articles.map((article) => (
              <div key={article.id}>
                <Link to={`/detail/${article.id}`}>
                  <img src={article.image} alt={`Image ${article.id}`} />
                </Link>
                <DataFetchingComponent apiId={article.id} />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default About;
